mapper.insert:
INSERT INTO APPROVAL_DETAIL ( APPR_DTL_ID , APPR_ID , PRDT_ID , CUR_STR , DEL_YN ) SELECT 'APPR_DT_' || TO_CHAR(SYSDATE, 'YYMMDD') || '_' || LPAD(SEQ_APPR_DT_PK.NEXTVAL, 6, '0'), AD.* FROM (
SELECT #{approvalDetailVO.apprId} AS APPR_ID , #{approvalDetailVO.prdtId} AS PRDT_ID , 1 AS CUR_STR , 'N' AS DEL_YN FROM DUAL
) AD

mapper.select:
SELECT COUNT(*)
  FROM APPROVAL_DETAIL
 WHERE APPR_ID = #{_parameter}

mapper.update:
UPDATE APPROVAL_DETAIL
   SET DEL_YN = 'Y'
 WHERE APPR_ID = #{_parameter}
