mapper.select:
SELECT O.OUT_ID , O.OUT_TYPE , O.OUT_TTL , O.OUT_VER , O.OUT_FILE , O.OUT_ENCODE_FILE , O.CRT_DT , O.CRTR_ID , O.MDF_DT , O.MDFR_ID , O.PRJ_ID , O.DEL_YN , CC.CMCD_NAME AS OUTPUT_TYPE_NAME , P.PRJ_NAME FROM PMS."OUTPUT" O JOIN COMMON_CODE CC ON O.OUT_TYPE = CC.CMCD_ID JOIN PROJECT P ON O.PRJ_ID = P.PRJ_ID WHERE O.DEL_YN='N'
AND O.PRJ_ID = #{prjId}
AND O.OUT_TYPE = #{outType}
ORDER BY O.OUT_ID DESC

	SELECT	count(1) AS LIST_CNT
 	  FROM	"OUTPUT" O
 	 WHERE	DEL_YN='N' 	 

	   AND 	O.PRJ_ID = #{prjId}


	   AND	O.OUT_TYPE = #{outType}

mapper.update:
UPDATE PMS."OUTPUT"
  SET  OUT_TYPE=#{outType} 
     , OUT_TTL=#{outTtl} 
     , OUT_VER=0 
     , OUT_FILE=#{outFile}
     , MDF_DT= SYSDATE
     , MDFR_ID='00000'  
     , OUT_ENCODE_FILE=#{outEncodeFile}  
 WHERE OUT_ID=#{outId}
